#Calculation of the maximum distance between two points

def diffmax_x(d):
    x = []
    for i in range (len(d)):
        for j in range(i) :
            x.append(abs(d[i][1]-d[j][1]))
    return max(x)


def diffmax_y(d):
    y = []
    for i in range (len(d)):
        for j in range(i) :
            y.append(abs(d[i][2]-d[j][2]))
    return max(y)


# Adaptation of the map scale

def ab(X,Y,x,y):
    a = (Y-X)/(y-x)
    b = X - a*x
    return[a,b]

def cd(X,Y,x,y):
    c = (Y-X)/(y-x)
    d = X - c*x
    return[c,d]


# Plot of Finland map

def trace_finland_map(M):
    im = plt.imread('finland_map.gif')
    implot = plt.imshow(im)
    plt.ion()
    a = ab(490,554,23.4536,24.567)[0]
    b = ab(490,554,23.4536,24.567)[1]
    c = cd(1081,1237,61.2953,60.1010)[0]
    d = cd(1081,1237,61.2953,60.1010)[1]
    for i in range(len(M)):
        x = M[i][2]*a + b
        y = M[i][1]*c + d
        plt.scatter(x, y, color="black")
        plt.pause(1)
    plt.ioff()
    plt.show()
    return






# Plot of Tampere map

def trace_tampere(P):
    im = plt.imread('tampere_map.gif')  # dim 1000 y 2000 x
    implot = plt.imshow(im)
    plt.ion()
    a = 0
    b = 0
    c = 0
    d = 0
    for i in range(len(P)):
        x = P[i][2]*a - b
        y = P[i][1]*c - d
        plt.scatter(x, y, color="black")
        plt.pause(1)
    plt.ioff()
    plt.show()
    return


# Precision

def precision(P,Q):

    plt.ion()

    for i in range(min(len(P),len(Q))):
        x = P[i][2]
        y = P[i][1]
        plt.scatter(x, y, color="black")
        z = Q[i][2]
        t = Q[i][1]
        plt.scatter(z, t, color="yellow")
        plt.pause(1)
    plt.ioff()
    plt.show()
    return


