# Canvas

def map():
    fenetre = Tk()
    photo = PhotoImage(file="tut_map.gif")

    canvas = Canvas(fenetre, width=750, height=700, background='white')
    canvas.create_window(80,80)
    canvas.create_image(0, 0, anchor=NW, image=photo)
    canvas.pack()

    bouton=Button(fenetre, text="Fermer", command=fenetre.quit)
    bouton.pack()

    liste = Listbox(fenetre)
    liste.insert(1, "Finland")
    liste.insert(2, "Tampere")
    liste.pack()






    fenetre.mainloop()
    return

